
"use strict"




var doc = `
Usage:
    jst [-s | --suppress-errors] [--] <selector> -
    jst [-s | --suppress-errors] [--] <selector> <path>...
    jst (-h | --help | --version)

Description:
    jst — pronounced jist — is a jQuery-like selector tool for destructing JavaScript programs
    into a UNIX command-line friendly format.

Search output:
    -s, --suppress-errors           Suppress error messages about nonexistant or unreadable files.

Miscellaneous:
    -h, --help                      Display this documentation.

Arguments:
    <selector>                      A selector to apply to the input JavaScript program(s).
    <path>...                       Paths pointing to JavaScript files to read.
`





var docopt = require('docopt').docopt
var jst    = require('jst/app/jst')
var args   = docopt(doc)





jst(args)
