
"use strict"




var doc = `
Usage:
    jst [-s | --suppress-errors] [--] -
    jst [-s | --suppress-errors] [--] <path>...
    jst (-h | --help | --version)

Searching:
    -i, --ignore-case               Ignore pattern casing.
    -v, --invert-match              Select non-matching lines.
    -Q, --literal                   Treat the input pattern as a literal.

Search output:
    -o                              Show the matching part of each line.
    -A NUM, --after-context NUM     Print NUM lines of trailing context after matching lines.
    -B NUM, --before-context NUM    Print NUM lines of trailing context after matching lines.
    -s, --suppress-errors           Suppress error messages about nonexistant or unreadable files.

Miscellaneous:
    -h, --help                      Display this documentation.

Arguments:
    <path>...                       Paths pointing to JavaScript files to read.
`





var docopt = require('docopt').docopt
var jst    = require('jst/app/jst')
var args   = docopt(doc)





jst(args)
