
"use strict"




var readline = require('readline')





var io = { }




io.readStdin = callback => {

	var lines = [ ]

	readline
	.createInterface({
		input: process.stdin
	})
	.on('line', line => {
		lines.push(line)
	})
	.on('close', ( ) => {
		callback(null, lines.join('\n'))
	})

}





module.exports = io
