
"use strict"





var fs = require('fs')





var io = require('jst/io/io')





var readInput = (readStdin, paths, callback) => {

	if (readStdin) {

		io.readStdin(callback)

	} else {

		paths.forEach(fpath => {

			fs.readFile(fpath, (err, fcontent) => {
				callback(err, fcontent.toString( ))
			})

		})

	}

}





module.exports = readInput
