
"use strict"



var groupTokens = tokens => {

	return tokens.reduce((acc, token) => {

		if (acc.length === 0 || acc[acc.length - 1].state !== token.state) {

			acc.push(token)

		} else {

			acc[acc.length - 1].char += token.char

		}

		return acc

	}, [ ])

}




module.exports = groupTokens
