
"use strict"




var tokeniseSelector = require('jst/selector/scan-selector')
var groupTokens      = require('jst/selector/group-tokens')
var filterStates     = require('jst/selector/filter-states')



var parseSelector = selector => {

	var tokens   = tokeniseSelector(selector)
	var filtered = [
		'WHITESPACE',
		'PSEUDOCLASS_OPEN',
		'PSEUDOCLASS_ARGUMENTS_OPEN',
		'PSEUDOCLASS_ARGUMENT_SEPERATOR',
		'PSEUDOCLASS_ARGUMENTS_CLOSE',
		'ATTRIBUTE_OPEN',
		'ATTRIBUTE_KEY',
		'STRING_OPEN',
		'STRING_CLOSE',
		'ATTRIBUTE_CLOSE',
		'RULE_SEPERATOR'
	]

	console.log(
		filterStates(groupTokens(tokens), filtered)
	)

}




module.exports = parseSelector
